    w HtmlFrag
    s SrcCode=$e(SrcCode,$l(HtmlFrag)+1+$l(Beg),$l(SrcCode) ) 
    s CodeFrag=$e(SrcCode,1, $l($p(SrcCode,End,1)) ) 
    s SrcCode=$e(SrcCode,$l(CodeFrag)+1+$l(End),$l(SrcCode) ) 
    if $l(CodeFrag)>0{
       TRY {  x $tr(CodeFrag,$c(13,10),"   ")   } CATCH ErrorHandle {    zw ErrorHandle  }
    }
  q:SrcCode=""
}

